// Extract and structure the incoming VAPI call data
const inputData = $input.all()[0].json;

// Extract call information
const callData = {
  callId: inputData.call?.id || inputData.id,
  candidateName: inputData.call?.metadata?.candidateName || inputData.metadata?.candidateName || 'Unknown Candidate',
  sessionId: inputData.call?.metadata?.sessionId || inputData.metadata?.sessionId,
  duration: inputData.call?.endedAt && inputData.call?.startedAt ? 
    new Date(inputData.call.endedAt).getTime() - new Date(inputData.call.startedAt).getTime() : 0,
  transcript: inputData.call?.transcript || inputData.transcript || '',
  recordingUrl: inputData.call?.recordingUrl || inputData.recordingUrl,
  startTime: inputData.call?.startedAt || inputData.startedAt,
  endTime: inputData.call?.endedAt || inputData.endedAt,
  callStatus: inputData.call?.status || inputData.status || 'unknown',
  phoneNumber: inputData.call?.phoneNumber || inputData.phoneNumber,
  cost: inputData.call?.cost || inputData.cost || 0,
  summary: inputData.call?.summary || inputData.summary,
  rawData: inputData
};

// Basic validation
if (!callData.transcript) {
  throw new Error('No transcript found in VAPI data');
}

if (!callData.candidateName || callData.candidateName === 'Unknown Candidate') {
  console.warn('Candidate name not found in metadata');
}

return [{ json: { callData } }];

