// Calculate comprehensive statistics and combine all data
const callData = $node['Extract Call Data'].json.callData;
const aiResponse = $node['AI Analysis'].json;

// Parse AI analysis (handle potential JSON parsing issues)
let aiAnalysis;
try {
  if (typeof aiResponse.choices[0].message.content === 'string') {
    aiAnalysis = JSON.parse(aiResponse.choices[0].message.content);
  } else {
    aiAnalysis = aiResponse.choices[0].message.content;
  }
} catch (error) {
  console.error('Error parsing AI response:', error);
  // Fallback analysis
  aiAnalysis = {
    overallScore: 5,
    communicationSkillsScore: 5,
    enthusiasmScore: 5,
    professionalismScore: 5,
    keyStrengths: ['Participated in interview'],
    areasForImprovement: ['Analysis could not be completed'],
    executiveSummary: 'Interview completed but detailed analysis unavailable.',
    recommendation: 'Interview Further',
    detailedFeedback: 'AI analysis failed - manual review required.',
    redFlags: [],
    standoutMoments: []
  };
}

// Calculate detailed statistics
const transcript = callData.transcript || '';
const words = transcript.split(/\s+/).filter(word => word.length > 0);
const sentences = transcript.split(/[.!?]+/).filter(s => s.trim().length > 0);
const durationMinutes = callData.duration > 0 ? callData.duration / 60000 : 0;

const statistics = {
  callDurationMinutes: Math.round(durationMinutes * 100) / 100,
  callDurationSeconds: Math.round(callData.duration / 1000),
  wordCount: words.length,
  sentenceCount: sentences.length,
  averageWordsPerMinute: durationMinutes > 0 ? Math.round((words.length / durationMinutes) * 100) / 100 : 0,
  averageWordsPerSentence: sentences.length > 0 ? Math.round((words.length / sentences.length) * 100) / 100 : 0,
  callCompletionRate: callData.callStatus === 'completed' ? 100 : 0,
  hasRecording: !!callData.recordingUrl,
  transcriptLength: transcript.length,
  estimatedCost: callData.cost || 0
};

// Calculate composite scores
const compositeScores = {
  averageScore: Math.round(((aiAnalysis.overallScore + aiAnalysis.communicationSkillsScore + aiAnalysis.enthusiasmScore + aiAnalysis.professionalismScore) / 4) * 100) / 100,
  skillsScore: Math.round(((aiAnalysis.communicationSkillsScore + aiAnalysis.professionalismScore) / 2) * 100) / 100,
  personalityScore: Math.round(((aiAnalysis.enthusiasmScore + aiAnalysis.overallScore) / 2) * 100) / 100
};

// Determine hire probability based on scores
let hireProbability = 0;
if (aiAnalysis.recommendation === 'Hire') hireProbability = 85;
else if (aiAnalysis.recommendation === 'Interview Further') hireProbability = 60;
else hireProbability = 25;

// Add score-based adjustments
if (compositeScores.averageScore >= 8) hireProbability += 10;
else if (compositeScores.averageScore <= 4) hireProbability -= 15;

hireProbability = Math.max(0, Math.min(100, hireProbability));

// Create comprehensive report data
const reportData = {
  // Candidate Info
  candidateName: callData.candidateName,
  sessionId: callData.sessionId,
  callId: callData.callId,

  // Call Details
  startTime: callData.startTime,
  endTime: callData.endTime,
  callStatus: callData.callStatus,
  phoneNumber: callData.phoneNumber,

  // AI Analysis Scores
  overallScore: aiAnalysis.overallScore,
  communicationScore: aiAnalysis.communicationSkillsScore,
  enthusiasmScore: aiAnalysis.enthusiasmScore,
  professionalismScore: aiAnalysis.professionalismScore,

  // Composite Scores
  averageScore: compositeScores.averageScore,
  skillsScore: compositeScores.skillsScore,
  personalityScore: compositeScores.personalityScore,
  hireProbability: hireProbability,

  // Qualitative Analysis
  keyStrengths: aiAnalysis.keyStrengths || [],
  areasForImprovement: aiAnalysis.areasForImprovement || [],
  executiveSummary: aiAnalysis.executiveSummary || '',
  recommendation: aiAnalysis.recommendation || 'Review Required',
  detailedFeedback: aiAnalysis.detailedFeedback || '',
  redFlags: aiAnalysis.redFlags || [],
  standoutMoments: aiAnalysis.standoutMoments || [],

  // Statistics
  ...statistics,

  // Technical Data
  transcript: callData.transcript,
  recordingUrl: callData.recordingUrl,

  // Metadata
  generatedAt: new Date().toISOString(),
  processedBy: 'n8n-workflow',
  workflowVersion: '1.0'
};

return [{
  json: {
    reportData,
    callData,
    aiAnalysis,
    statistics,
    compositeScores
  }
}];

// Generate comprehensive HTML report
const reportData = $node['Calculate Statistics & Compile Report'].json.reportData;

// Helper function to get score color
function getScoreColor(score) {
  if (score >= 8) return '#10b981'; // Green
  if (score >= 6) return '#f59e0b'; // Yellow
  return '#ef4444'; // Red
}

// Helper function to get recommendation color
function getRecommendationColor(recommendation) {
  if (recommendation === 'Hire') return '#10b981';
  if (recommendation === 'Interview Further') return '#f59e0b';
  return '#ef4444';
}

