// Enhanced Debug: Log everything VAPI sends us
const inputData = $input.all()[0].json;

console.log('=== VAPI WEBHOOK DATA RECEIVED ===');
console.log('Full payload:', JSON.stringify(inputData, null, 2));
console.log('Event type:', inputData.type);
console.log('Message type:', inputData.message?.type);
console.log('Call ID:', inputData.call?.id || inputData.message?.call?.id);

// Check all possible transcript locations
console.log('Direct transcript:', inputData.transcript);
console.log('Message transcript:', inputData.message?.transcript);
console.log('Call transcript:', inputData.call?.transcript);
console.log('Message call transcript:', inputData.message?.call?.transcript);

// Check for conversation messages
if (inputData.messages) {
  console.log('Conversation messages found:', inputData.messages.length);
  console.log('Messages:', JSON.stringify(inputData.messages, null, 2));
}
if (inputData.call?.messages) {
  console.log('Call messages found:', inputData.call.messages.length);
  console.log('Call messages:', JSON.stringify(inputData.call.messages, null, 2));
}
if (inputData.message?.call?.messages) {
  console.log('Message call messages found:', inputData.message.call.messages.length);
  console.log('Message call messages:', JSON.stringify(inputData.message.call.messages, null, 2));
}

// Check for analysis/summary
if (inputData.analysis || inputData.call?.analysis || inputData.message?.call?.analysis) {
  console.log('Analysis found:', inputData.analysis || inputData.call?.analysis || inputData.message?.call?.analysis);
}

console.log('Metadata:', inputData.metadata || inputData.call?.metadata || inputData.message?.call?.metadata);
console.log('=====================================');

// Pass through unchanged
return [{ json: inputData }];

// Enhanced VAPI Data Extraction for Interview Processing
const inputData = $input.all()[0].json;

console.log('=== PROCESSING VAPI END-OF-CALL-REPORT ===');
console.log('Message type:', inputData.message?.type);
console.log('Call ID:', inputData.message?.call?.id);

// Extract the main message data
const message = inputData.message;
const call = message?.call;
const analysis = message?.analysis;
const artifact = message?.artifact;

// Extract transcript from multiple possible locations
let transcript = '';
if (message?.transcript) {
  transcript = message.transcript;
  console.log('📝 Found transcript in message.transcript');
} else if (artifact?.transcript) {
  transcript = artifact.transcript;
  console.log('📝 Found transcript in artifact.transcript');
} else if (call?.transcript) {
  transcript = call.transcript;
  console.log('📝 Found transcript in call.transcript');
} else {
  // Build transcript from messages if no direct transcript
  const messages = artifact?.messages || message?.messages || [];
  if (messages.length > 0) {
    transcript = messages
      .filter(msg => msg.role === 'bot' || msg.role === 'user' || msg.role === 'assistant')
      .map(msg => {
        const role = msg.role === 'bot' ? 'AI' : (msg.role === 'assistant' ? 'AI' : 'User');
        return `${role}: ${msg.message}`;
      })
      .join('\n');
    console.log('📝 Built transcript from messages array');
  }
}

// Extract summary
const summary = analysis?.summary || message?.summary || call?.summary || 'No summary available';

// Extract call statistics
const callStats = {
  callId: call?.id || message?.call?.id || 'unknown',
  duration: {
    milliseconds: message?.durationMs || 0,
    seconds: message?.durationSeconds || 0,
    minutes: message?.durationMinutes || 0
  },
  startedAt: message?.startedAt || call?.startedAt,
  endedAt: message?.endedAt || call?.endedAt,
  endedReason: message?.endedReason || 'unknown',
  totalCost: message?.cost || 0,
  costBreakdown: message?.costBreakdown || {},
  recordingUrl: message?.recordingUrl || artifact?.recordingUrl,
  stereoRecordingUrl: message?.stereoRecordingUrl || artifact?.stereoRecordingUrl
};

// Extract candidate information
const candidateName = 
  call?.metadata?.candidateName || 
  message?.assistant?.variableValues?.candidateName ||
  message?.assistant?.variableValues?.name ||
  'Unknown Candidate';

const sessionId = 
  call?.metadata?.sessionId || 
  message?.assistant?.variableValues?.sessionId ||
  'unknown';

// Extract session token for Airtable lookup
const sessionToken = 
  call?.metadata?.sessionToken || 
  message?.assistant?.variableValues?.sessionToken ||
  message?.assistant?.metadata?.sessionToken ||
  call?.metadata?.sessionId || // fallback to sessionId
  message?.assistant?.variableValues?.sessionId ||
  null;

console.log('🔖 Session Token extracted:', sessionToken);
console.log('👤 Candidate Name extracted:', candidateName);

// Structure all extracted data
const extractedData = {
  eventType: message?.type || 'unknown',
  timestamp: message?.timestamp || Date.now(),

  candidate: {
    name: candidateName,
    sessionId: sessionId,
    sessionToken: sessionToken  // Add for Airtable lookup
  },

  transcript: {
    full: transcript,
    length: transcript.length,
    hasContent: transcript.length > 0
  },

  analysis: {
    summary: summary,
    successEvaluation: analysis?.successEvaluation || 'unknown'
  },

  callStats: callStats,

  conversation: {
    messages: artifact?.messages || [],
    totalMessages: (artifact?.messages || []).length
  },

  processing: {
    extractedAt: new Date().toISOString(),
    hasTranscript: transcript.length > 0,
    hasSummary: summary !== 'No summary available'
  }
};

console.log('📊 EXTRACTION SUMMARY:');
console.log(`- Candidate: ${extractedData.candidate.name}`);
console.log(`- Transcript length: ${extractedData.transcript.length} characters`);
console.log(`- Call duration: ${extractedData.callStats.duration.minutes} minutes`);
console.log(`- Total cost: $${extractedData.callStats.totalCost}`);
console.log(`- Success evaluation: ${extractedData.analysis.successEvaluation}`);
console.log('=== EXTRACTION COMPLETE ===');

return [{ json: { extractedData } }];

